import React, { useState } from 'react';
import axios from 'axios';
import segmented_image_face from './segmented_image_face.jpg';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [segmentedImageUrl, setSegmentedImageUrl] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };
  console.log("segmented_image_face",segmented_image_face)

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a file');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);


    try {
      // Upload image to backend and store locally
      const uploadResponse = await axios.post('http://127.0.0.1:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      // Get the stored image URL
      const storedImageUrl = uploadResponse.data.storedImageUrl;

      // Set the stored image URL
      setImageUrl(storedImageUrl);
      setSegmentedImageUrl('');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleSegmentation = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/segment', { imageUrl });

      setSegmentedImageUrl(response.data.segmentedImageUrl);
    } catch (error) {
      console.error('Error:', error);
    }
  };
  console.log("setSegmentedImageUrl", segmentedImageUrl)

  return (
    <div className="container">
      <h1>Face Segmentation</h1>
      <div className="form-container">
        <input type="file" onChange={handleFileChange} accept="image/*" />
        <button onClick={handleUpload}>Upload</button>
      </div>
      {imageUrl && (
        <div className="form-container">
          <button onClick={handleSegmentation}>Segment</button>
        </div>
      )}
      {true && (
        <div className="segmented-image">
          <h2>Segmented Image</h2>
          <img src={segmented_image_face} alt="Segmented Face" width="500px" height={"500px"}/>
        </div>
      )}
    </div>
  );
}

export default App;
