import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [segmentedImageUrl, setSegmentedImageUrl] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setSegmentedImageUrl(URL.createObjectURL(response.data));
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container">
      <h1>Face Segmentation</h1>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <input type="file" onChange={handleFileChange} accept="image/*" />
          <button type="submit">Upload</button>
        </form>
      </div>
      {segmentedImageUrl && (
        <div className="segmented-image">
          <h2>Segmented Image</h2>
          <img src={segmentedImageUrl} alt="Segmented Face" />
        </div>
      )}
    </div>
  );
}

export default App;
