import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
// import firebase from 'firebase/app';
// import 'firebase/storage';

const firebaseConfig = {
  apiKey: "YOUR_API_KEY",
  authDomain: "YOUR_AUTH_DOMAIN",
  projectId: "YOUR_PROJECT_ID",
  storageBucket: "YOUR_STORAGE_BUCKET",
  messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
  appId: "YOUR_APP_ID"
};

firebase.initializeApp(firebaseConfig);

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [segmentedImageUrl, setSegmentedImageUrl] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a file');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://127.0.0.1:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      const imageUrl = response.data.imageUrl;
      setImageUrl(imageUrl);
      setSegmentedImageUrl('');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleSegmentation = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/segment', { imageUrl });

      setSegmentedImageUrl(response.data.segmentedImageUrl);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container">
      <h1>Face Segmentation</h1>
      <div className="form-container">
        <input type="file" onChange={handleFileChange} accept="image/*" />
        <button onClick={handleUpload}>Upload</button>
      </div>
      {imageUrl && (
        <div className="form-container">
          <button onClick={handleSegmentation}>Segment</button>
        </div>
      )}
      {segmentedImageUrl && (
        <div className="segmented-image">
          <h2>Segmented Image</h2>
          <img src={segmentedImageUrl} alt="Segmented Face" />
        </div>
      )}
    </div>
  );
}

export default App;
