import React, { useState } from 'react';
import axios from 'axios';
import firebase from 'firebase/app';
import 'firebase/storage';
import './App.css';

// Initialize Firebase
const firebaseConfig = {
  apiKey: "AIzaSyCIwBxHmlGI9P4mTZc2N_m3vaCCUKua8Iw",
  authDomain: "face-segmentation-3c4e3.firebaseapp.com",
  projectId: "face-segmentation-3c4e3",
  storageBucket: "face-segmentation-3c4e3.appspot.com",
  messagingSenderId: "350667346722",
  appId: "1:350667346722:web:e3b8e10e0d09ef51358b7b",
  measurementId: "G-2KZDXK5LC4"
};
firebase.initializeApp(firebaseConfig);
const storage = firebase.storage();

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [segmentedImage, setSegmentedImage] = useState('');
  const [segmentedImageUrl, setSegmentedImageUrl] = useState(0);
  const [file, setFile] = useState("");
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setFile(URL.createObjectURL(event.target.files[0]));
  };

  console.log("selectedFile", selectedFile)

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a file');
      return;
    }

    try {
      // Upload image to Firebase Storage
      const storageRef = storage.ref();
      const imageRef = storageRef.child(`images/${selectedFile.name}`);
      await imageRef.put(selectedFile);

      // Get the download URL
      const downloadURL = await imageRef.getDownloadURL();

      // Send the download URL to the backend
      const response = await axios.post('http://127.0.0.1:5000/upload', { imageUrl: downloadURL });

      // Set the stored image URL
      setImageUrl(downloadURL);
      setSegmentedImageUrl(0);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleSegmentation = async () => {
    try {
      setLoading(true);
      const response = await axios.post('http://127.0.0.1:5000/segment', { imageUrl });
      if (response.data.segmentedImageUrl) {
        setSegmentedImage(response.data.segmentedImageUrl);
        setSegmentedImageUrl((prev) => prev + 1);
        setLoading(false);
      }
    } catch (error) {
      setLoading(false);
      console.error('Error:', error);
    }
  };
  console.log("setSegmentedImageUrl", segmentedImageUrl)

  return (
    <div className="container">
      <h1>Face Segmentation</h1>
      <div className="form-container">
        <div className='upload-container'>
          {file ? <img src={file} width="250px" height={"250px"} /> : null}
          <label htmlFor='uploadImage' className='upload-button'>
            Choose File
            <input
              type="file"
              onChange={handleFileChange}
              accept="image/*"
              style={{ display: 'none' }}
              id="uploadImage"
            />
          </label>
          <button onClick={handleUpload} className='upload-button'>Upload</button>
        </div>
      </div>
      {imageUrl && (
        <div className="form-container">
          <button onClick={handleSegmentation} className='upload-button'>Segment</button>
        </div>
      )}
      {loading ? <div>Please Wait, Segmenting the Image</div> : null}
      {segmentedImageUrl ? (
        <div className="segmented-image">
          <h2>Segmented Image</h2>
          <img src={segmentedImage} alt="Segmented Face" width="500px" height={"300px"} />
        </div>
      ) : null}
    </div>
  );
}

export default App;
